package com.example.the_best_quiz_ever.controllers;import com.example.the_best_quiz_ever.model_DTOs.*;import com.example.the_best_quiz_ever.models.Outcome;import com.example.the_best_quiz_ever.models.Question;import com.example.the_best_quiz_ever.models.Quiz;import com.example.the_best_quiz_ever.services.QuizService;// import jakarta.xml.ws.http.HTTPBinding;import jakarta.transaction.Transactional;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.ArrayList;import java.util.List;import java.util.Optional;import java.util.ResourceBundle;@RestController@RequestMapping(value = "quizzes")public class QuizController {    @Autowired    QuizService quizService;    //maybe will have to autowire more classes    //    display available quizzes - GET    @GetMapping    public ResponseEntity<List<Quiz>> getAllQuizzes() {        return new ResponseEntity<>(quizService.getAllQuizzes(), HttpStatus.OK);    }//* select quiz using id- GET//    return reply- display quiz by id    @GetMapping(value = "/{id}") //change to post?    public ResponseEntity<Quiz> getQuizById(@PathVariable Long id) {        Optional<Quiz> quizByID = quizService.getQuizById(id);        if (quizByID.isEmpty()){            return new ResponseEntity<>(HttpStatus.NOT_FOUND);        } else {            return new ResponseEntity<>(quizByID.get(), HttpStatus.OK);        }    }//   start quiz and show question 1    @PostMapping(value = "/start-new-game") //starts the quiz and gives first question    public ResponseEntity<Reply> createQuiz(@RequestBody QuizDTO quizDTO) {    return new ResponseEntity<>(quizService.startQuiz(quizDTO.getId()), HttpStatus.CREATED);}//* choose option- PATCH, (request body letter)//        return reply -  answered questions message    @PatchMapping(value = "/{id}")    public ResponseEntity<Optional<Reply>> selectAnswer(@RequestBody ChoiceDTO choiceDTO, @PathVariable Long id) {        Reply reply = quizService.processAnswer(choiceDTO.getAnswerId(), choiceDTO.getqNumber(), id);        return new ResponseEntity(reply, HttpStatus.OK);    }    @PostMapping(value = "/add-new-quiz")    public ResponseEntity<Quiz> addNewQuiz(@RequestBody AddNewQuizDTO addNewQuizDTO){        Quiz quiz = quizService.addNewQuiz(addNewQuizDTO);    return new ResponseEntity<>(quiz, HttpStatus.CREATED);    }    @PostMapping(value = "/add-question-to-quiz")    public ResponseEntity<String> addNewQuestion(@RequestBody AddNewQuestionDTO addNewQuestionDTO){        String response = quizService.addNewQuestion(addNewQuestionDTO);        return new ResponseEntity<>(response, HttpStatus.CREATED);    }    @PostMapping(value = "/add-answer-to-question")    public ResponseEntity<String> addAnswerToQuestion(@RequestBody AddNewAnswerDTO addNewAnswerDTO){        String response = quizService.addAnswerToQuestion(addNewAnswerDTO);        return new ResponseEntity<>(response, HttpStatus.CREATED);    }    @PostMapping(value = "/add-outcome")    public ResponseEntity<String> addOutcomeToQuiz(@RequestBody AddNewOutcomeDTO addNewOutcomeDTO){        String response = quizService.addOutcomeToQuiz(addNewOutcomeDTO);        return new ResponseEntity<>(response, HttpStatus.CREATED);    }    @GetMapping(value = "/get-all-outcomes")    public ResponseEntity<List<Outcome>> getAllOutcomes(){        List<Outcome> listOfOutcomes = quizService.getAllOutcomes();        return new ResponseEntity<>(listOfOutcomes, HttpStatus.OK);    }    @GetMapping(value = "/get-all-questions")    public ResponseEntity<List<Question>> getAllQuestions(){        List<Question> listOfQuestions = quizService.getAllQuestions();        return new ResponseEntity<>(listOfQuestions, HttpStatus.OK);    }//    display question 2 with options//* continue for all 10 Qs//* after 10th: return reply: Outcome}